import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import API from "../utils/api";
import "../styles/Home.css";
import ExclusiveOffers from "../components/Home/ExclusiveOffers"; // Add this import for the exclusive offers

const Home = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchCars = async () => {
      try {
        const { data } = await API.get("/cars");
        // Filter out unapproved cars and only show a limited number
        setCars(data.filter((car) => car.isApproved).slice(0, 5));
        setLoading(false);
      } catch (err) {
        console.error("Failed to fetch cars:", err);
        setLoading(false);
      }
    };
    fetchCars();
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    // Redirect to the cars page with the search term
    navigate(`/cars?search=${searchTerm}`);
  };

  return (
    <body>
      <div className="home-container">
      {/* Search Container */}
      <div className="hero-section">
        <div className="search-container">
          <h1 className="search-title">Find Your Dream Car</h1>
          <p className="search-subtitle">
            Search by brand, model, or features.
          </p>
          <form className="search-form" onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search by brand or model..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button type="submit" className="search-btn">Search</button>
          </form>
        </div>
      </div>

      {/* Car Listings */}
      <div className="content-section">
        <h2 className="section-title">Latest Listings</h2>
        {loading ? (
          <p>Loading cars...</p>
        ) : (
          <div className="car-grid">
            {cars.length > 0 ? (
              cars.map((car) => (
                <div key={car._id} className="car-card">
                  <img
                    src={car.image}
                    alt={`${car.brand} ${car.model}`}
                    className="car-image"
                  />
                  <div className="car-info">
                    <h3>{car.brand} {car.model}</h3>
                    <p>Price: ${car.price}</p>
                    <p>Year: {car.year}</p>
                  </div>
                </div>
              ))
            ) : (
              <p>No cars available at the moment.</p>
            )}
          </div>
        )}
      </div>

      {/* Exclusive Offers */}
      <div className="content-section">
        <h2 className="section-title">Exclusive Offers</h2>
        <ExclusiveOffers />
      </div>
    </div>
    </body>
  );
};

export default Home;
