import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import API from "../utils/api";
import "../styles/Home.css"; // A new CSS file for the home page

// This is a new component for the exclusive offers carousel
import ExclusiveOffers from "../components/Home/ExclusiveOffers"; 

const Home = () => {
const [searchTerm, setSearchTerm] = useState("");
const [cars, setCars] = useState([]);
const [loading, setLoading] = useState(true);
const [error, setError] = useState(null);
const navigate = useNavigate();

useEffect(() => {
const fetchCars = async () => {
try {
const { data } = await API.get("/cars");
setCars(data.slice(0, 8)); // Displaying a limited number of cars
setLoading(false);
} catch (err) {
console.error("Failed to fetch cars:", err);
setError("Failed to load car listings.");
setLoading(false);
}
};
fetchCars();
}, []);

const handleSearch = (e) => {
e.preventDefault();
// Redirect to the cars page with the search term as a query parameter
navigate(`/cars?search=${searchTerm}`);
};

return (
<div className="home-container">
<div className="hero-section">
<div className="hero-content">
<h1>Find Your Dream Car Today</h1>
<p>Explore a wide range of verified used cars with transparent pricing and secure transactions.</p>
<form className="search-form" onSubmit={handleSearch}>
<input
type="text"
placeholder="Search by brand or model..."
value={searchTerm}
onChange={(e) => setSearchTerm(e.target.value)}
/>
<button type="submit" className="search-btn">Search</button>
</form>
</div>
</div>

<div className="content-section">
<ExclusiveOffers />
</div>

<div className="content-section">
<h2 className="section-title">Latest Listings</h2>
{loading && <p>Loading cars...</p>}
{error && <p className="error-message">{error}</p>}
{!loading && !error && (
<div className="car-grid">
{cars.length > 0 ? (
cars.map((car) => (
<div key={car._id} className="car-card">
<img src={car.image} alt={car.title} className="car-image" />
<h3>{car.brand} {car.model}</h3>
<p>
<strong>Price:</strong> ${car.price}
</p>
<p>
<strong>Year:</strong> {car.year}
</p>
</div>
))
) : (
<p>No cars available at the moment.</p>
)}
</div>
)}
</div>
</div>
);
};

export default Home;
