import React, { useState, useEffect } from "react";
import "../../styles/ExclusiveOffers.css";

const offers = [
 {
   id: 1,
   title: "Summer Sale!",
   description: "20% off all sedan models. Limited time offer!",
   image: "https://via.placeholder.com/800x400/2980b9/ffffff?text=Summer+Sale"
 },
 {
   id: 2,
   title: "Special Financing",
   description: "0% APR on select luxury brands for 60 months.",
   image: "https://via.placeholder.com/800x400/27ae60/ffffff?text=Special+Financing"
 },
 {
   id: 3,
   title: "Trade-in Bonus",
   description: "Get an extra $1,500 on your trade-in value.",
   image: "https://via.placeholder.com/800x400/8e44ad/ffffff?text=Trade-in+Bonus"
 }
];

const ExclusiveOffers = () => {
 const [currentIndex, setCurrentIndex] = useState(0);

 const nextSlide = () => {
   setCurrentIndex((prevIndex) => (prevIndex + 1) % offers.length);
 };

 const prevSlide = () => {
   setCurrentIndex((prevIndex) =>
     prevIndex === 0 ? offers.length - 1 : prevIndex - 1
   );
 };

 // Automatic slide change every 5 seconds
 useEffect(() => {
   const intervalId = setInterval(nextSlide, 5000);
   return () => clearInterval(intervalId);
 }, [currentIndex]);

 return (
   <div className="exclusive-offers-container">
     <h2 className="section-title">Exclusive Offers</h2>
     <div className="carousel">
       <button onClick={prevSlide} className="carousel-btn prev-btn">
         &lt;
       </button>
       <div className="carousel-content">
         <img
           src={offers[currentIndex].image}
           alt={offers[currentIndex].title}
           className="carousel-image"
         />
         <div className="offer-details">
           <h3>{offers[currentIndex].title}</h3>
           <p>{offers[currentIndex].description}</p>
         </div>
       </div>
       <button onClick={nextSlide} className="carousel-btn next-btn">
         &gt;
       </button>
     </div>
   </div>
 );
};

export default ExclusiveOffers;
