import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom"; // ✅ Import Link
import API from "../utils/api";
import "../styles/Home.css"; // We will modify this file
import ExclusiveOffers from "../components/Home/ExclusiveOffers";
import io from "socket.io-client";


const socket = io("http://localhost:5000");


const Home = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [cars, setCars] = useState([]);
  const [loading, setLoading] = useState(true);
  const [currentIndex, setCurrentIndex] = useState(0);  // For slider
  const navigate = useNavigate();

  const fetchCars = async () => {
    setLoading(true);
    try {
      const { data } = await API.get("/cars");
      // Filter out unapproved cars and only show the latest 10
      setCars(data.filter((car) => car.isApproved).slice(0, 10));
      setLoading(false);
    } catch (err) {
      console.error("Failed to fetch cars:", err);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchCars();

    socket.on("newCarApproved", (data) => {
      console.log("New car approved, re-fetching cars for homepage:", data);
      fetchCars();
    });

    return () => {
      socket.off("newCarApproved");
    };
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    navigate(`/cars?search=${searchTerm}`);
  };

  const nextSlide = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % cars.length);
  };

  const prevSlide = () => {
    setCurrentIndex((prevIndex) =>
      prevIndex === 0 ? cars.length - 1 : prevIndex - 1
    );
  };

  useEffect(() => {
    if (cars.length > 1) {
      const interval = setInterval(nextSlide, 5000);
      return () => clearInterval(interval);
    }
  }, [cars]);

  return (
    <div className="home-container">
      {/* Search Container */}
      <div className="hero-section">
        <div className="search-container">
          <h1 className="search-title">All the cars you need in one place</h1>
          <p className="search-subtitle">
            We have the most popular brands and models for your vehicle.
          </p>
          <form className="search-form" onSubmit={handleSearch}>
            <input
              type="text"
              placeholder="Search by brand or model..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
            />
            <button type="submit" className="search-btn">Search</button>
          </form>
        </div>
      </div>

      {/* Latest Cars Section */}
      <div className="content-section">
        <h2 className="section-title">Latest Listings</h2>
        {loading ? (
          <p>Loading cars...</p>
        ) : (
          <div className="latest-cars-container">
            <button onClick={prevSlide} className="carousel-btn prev-btn">
              &lt;
            </button>
            <div className="carousel-content">
              {cars.length > 0 && (
                <div className="carousel-cards">
                  {cars.map((car, index) => (
                    <Link
                      to={`/cars/${car._id}`}
                      key={car._id}
                      className={`car-card ${index === currentIndex ? "active" : ""}`}
                    >
                      <div className="car-card">
                        <img src={car.image} alt={`${car.brand} ${car.model}`} />
                        <div className="car-info">
                          <h3>{car.brand} {car.model}</h3>
                          <p>Price: ${car.price}</p>
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              )}
            </div>
            <button onClick={nextSlide} className="carousel-btn next-btn">
              &gt;
            </button>
          </div>
        )}
      </div>

      {/* Exclusive Offers */}
      <div className="content-section">
        <h2 className="section-title">Exclusive Offers</h2>
        <ExclusiveOffers />
      </div>
    </div>
  );
};

export default Home;
